package com.basicproblem;

public class ASCII_value_of_character {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'A';
		int integer = ch;
		System.out.println("ASCII value of"+ch+" is : "+integer);
		
		int num = 98;
		char character = (char)num;
		System.out.println(num+" of ASCII value is "+character);

	}

}
