package com.basicproblem;

public class Smallest_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Smallest Number");
		int num1 = 10;
		int num2 = 30;
		
		if (num1 < num2) {
			System.out.println(num1);
		}
		else {
			System.out.println(num2);
		}

	}

}
