package com.basicproblem;

public class Total_surface_area_of_cylinder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int r = 7;
		int h = 13;
		double pi = 3.142;
		
		double area_of_cylinder = 2 * pi * r *(r + h);
		
		System.out.println(area_of_cylinder);

	}

}
