package com.basicproblem;

public class multiple_of_five {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 25;
		
		System.out.println("Multiple of 5");
		if (num%5 == 0) {
			System.out.println("Yes");
		}
		else {
			System.out.println("No");
		}

	}

}
