package com.basicproblem;

public class multiple_of_ten {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 50;
		
		System.out.println("Multiple of 10");
		if (num%10 == 0) {
			System.out.println("Yes");
		}
		else {
			System.out.println("No");
		}
		
	}

}
