package loopPractice;

public class first_n_odd_naturals_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("First N odd natural numbers : ");
		int num = 5;
		
		for (int i = 1;i <= num; i++) {
			
			System.out.println(i*2-1);
		}
		
	}

}
